public class TypeCasting {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		byte i=20;
		byte j=30;
		int k=i+j;
		System.out.println(k);
		
		byte x=10;
		int y=x;
		System.out.println(x);
		
		float t=20.16f;
		int r=(int)t;
		System.out.println(r);
		
		
		
	}

}
